import React, { useEffect, useState } from "react";
import questions from "./data/questions.json";
import { motion } from "framer-motion";
import { ArrowLeft, ArrowRight } from "lucide-react";

const App = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedWords, setSelectedWords] = useState([]);
  const [feedback, setFeedback] = useState([]);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [timeLeft, setTimeLeft] = useState(30);

  const currentQuestion = questions[currentQuestionIndex];

  const handleWordClick = (word) => {
    if (selectedWords.length < 4) {
      setSelectedWords([...selectedWords, word]);
    }
  };

  const handleWordUnselect = (word) => {
    setSelectedWords(selectedWords.filter((w) => w !== word));
  };

  const handleSubmit = () => {
    if (selectedWords.length === 4) {
      const isCorrect = JSON.stringify(selectedWords) === JSON.stringify(currentQuestion.correctAnswer);
      const answerFeedback = {
        question: currentQuestion,
        selectedWords,
        isCorrect,
      };
      setFeedback([...feedback, answerFeedback]);
      setIsSubmitted(true);
    }
  };

  const handleNextQuestion = () => {
    setIsSubmitted(false);
    setSelectedWords([]);
    setTimeLeft(30);
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
  };

  const handlePrevQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex((prev) => prev - 1);
      setIsSubmitted(false);
      setSelectedWords([]);
      setTimeLeft(30);
    }
  };

  useEffect(() => {
    if (isSubmitted || currentQuestionIndex >= questions.length) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev === 1) {
          handleSubmit();
          clearInterval(timer);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [isSubmitted, currentQuestionIndex]);

  if (currentQuestionIndex >= questions.length) {
    const finalScore = feedback.filter((item) => item.isCorrect).length;

    return (
      <div className="min-h-screen flex flex-col items-center justify-center p-6 bg-gradient-to-br from-purple-100 via-indigo-100 to-white">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="max-w-3xl w-full bg-white p-8 rounded-2xl shadow-lg"
        >
          <h1 className="text-4xl font-bold text-center text-purple-800 mb-6">
            üéâ Game Over!
          </h1>
          <p className="text-xl md:text-2xl mb-6 text-center text-gray-800">
            Your Score: <span className="font-semibold text-indigo-700">{finalScore}</span> / {questions.length}
          </p>

          {feedback.map((item, index) => (
            <div key={index} className="mb-6 border-b pb-4">
              <h2 className="font-semibold text-gray-800 mb-1">
                {index + 1}. {item.question.sentence}
              </h2>
              <p className="mb-1">
                <span className="font-medium">Result:</span>{" "}
                <span className={item.isCorrect ? "text-green-600" : "text-red-600"}>
                  {item.isCorrect ? "‚úÖ Correct" : "‚ùå Incorrect"}
                </span>
              </p>
              {!item.isCorrect && (
                <>
                  <p>
                    <span className="font-medium">Your Answer:</span>{" "}
                    {item.selectedWords.join(", ")}
                  </p>
                  <p>
                    <span className="font-medium">Correct Answer:</span>{" "}
                    {item.question.correctAnswer.join(", ")}
                  </p>
                </>
              )}
            </div>
          ))}
        </motion.div>
      </div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="min-h-screen flex flex-col items-center justify-center p-4 bg-gradient-to-br from-blue-100 via-white to-indigo-100"
    >
      <div className="w-full max-w-4xl bg-white rounded-2xl p-8 shadow-xl">
        <div className="flex justify-between items-center mb-4">
          <span className="text-gray-700 font-medium">
            Question {currentQuestionIndex + 1} of {questions.length}
          </span>
          <span className="text-red-600 font-semibold">‚è≥ {timeLeft}s</span>
        </div>

        <div className="w-full h-2 bg-gray-200 rounded-full mb-4">
          <div
            className="h-full bg-indigo-500 rounded-full"
            style={{
              width: `${((currentQuestionIndex + 1) / questions.length) * 100}%`,
              transition: "width 0.5s",
            }}
          />
        </div>

        <h2 className="text-2xl font-bold text-gray-800 mb-6">
          {currentQuestion.sentence}
        </h2>

        <div className="flex flex-wrap gap-3 mb-4">
          {currentQuestion.options.map((word, index) => {
            const isSelected = selectedWords.includes(word);
            return (
              <button
                key={index}
                onClick={() =>
                  isSelected ? handleWordUnselect(word) : handleWordClick(word)
                }
                className={`px-4 py-2 rounded-full font-medium shadow transition ${
                  isSelected
                    ? "bg-indigo-600 text-white"
                    : "bg-gray-100 hover:bg-indigo-100"
                }`}
              >
                {word}
              </button>
            );
          })}
        </div>

        <div className="mb-6">
          <p className="text-gray-700 font-semibold">Your Answer:</p>
          <div className="flex gap-2 flex-wrap mt-2">
            {selectedWords.map((word, idx) => (
              <span
                key={idx}
                className="px-3 py-1 bg-indigo-100 text-indigo-800 rounded-full"
              >
                {word}
              </span>
            ))}
          </div>
        </div>

        <div className="flex justify-between">
          <button
            onClick={handlePrevQuestion}
            disabled={currentQuestionIndex === 0}
            className="flex items-center gap-2 px-4 py-2 bg-gray-200 text-gray-700 rounded-xl hover:bg-gray-300 disabled:opacity-50"
          >
            <ArrowLeft size={18} /> Previous
          </button>

          {!isSubmitted ? (
            <button
              onClick={handleSubmit}
              disabled={selectedWords.length < 4}
              className="px-6 py-2 bg-indigo-600 text-white font-semibold rounded-xl hover:bg-indigo-700 disabled:opacity-50"
            >
              Submit Answer
            </button>
          ) : (
            <button
              onClick={handleNextQuestion}
              className="px-6 py-2 bg-green-600 text-white font-semibold rounded-xl hover:bg-green-700"
            >
              Next Question <ArrowRight size={18} className="inline ml-1" />
            </button>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default App;
