// App.js
import React, { useState, useEffect } from 'react';
import { fetchQuestions } from './data/api';
import QuestionCard from './components/QuestionCard';
import ScoreScreen from './components/ScoreScreen';
import { motion, AnimatePresence } from 'framer-motion';

export default function App() {
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [loading, setLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState(30);
  const [gameStarted, setGameStarted] = useState(false);

  useEffect(() => {
    fetchQuestions().then((data) => {
      setQuestions(data);
      setLoading(false);
    });
  }, []);

  useEffect(() => {
    if (!gameStarted || gameOver) return;

    if (timeLeft === 0) {
      setGameOver(true);
    }

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft, gameStarted, gameOver]);

  const handleAnswer = (isCorrect) => {
    if (isCorrect) setScore((prev) => prev + 1);

    const next = currentIndex + 1;
    if (next < questions.length) {
      setCurrentIndex(next);
      setTimeLeft(30);
    } else {
      setGameOver(true);
    }
  };

  const handleRestart = () => {
    setScore(0);
    setCurrentIndex(0);
    setGameOver(false);
    setTimeLeft(30);
    setGameStarted(false);
    setLoading(true);
    fetchQuestions().then((data) => {
      setQuestions(data);
      setLoading(false);
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-200 flex items-center justify-center p-4">
      <div className="max-w-xl w-full bg-white shadow-xl rounded-2xl p-8 transition-all duration-500">
        <AnimatePresence mode="wait">
          {!gameStarted ? (
            <motion.div
              key="start"
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              className="text-center"
            >
              <h1 className="text-4xl font-bold mb-6 text-blue-600">Sentence Construction Game</h1>
              <p className="mb-6 text-lg">Test your sentence construction skills!</p>
              <button
                className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-full transition"
                onClick={() => setGameStarted(true)}
              >
                Start Game
              </button>
            </motion.div>
          ) : loading ? (
            <div className="text-center font-medium text-gray-600">Loading...</div>
          ) : gameOver ? (
            <ScoreScreen score={score} total={questions.length} onRestart={handleRestart} />
          ) : (
            <motion.div
              key={currentIndex}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -30 }}
            >
              <div className="text-center">
                <div className="text-xl font-semibold text-red-500 mb-2">
                  Time Left: {timeLeft}s
                </div>

                {/* Progress Bar */}
                <div className="w-full bg-gray-200 rounded-full h-2 mb-4">
                  <div
                    className="bg-green-500 h-2 rounded-full transition-all"
                    style={{
                      width: `${((currentIndex + 1) / questions.length) * 100}%`,
                    }}
                  ></div>
                </div>

                <QuestionCard
                  question={questions[currentIndex]}
                  onAnswer={handleAnswer}
                />
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}
