import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [gameStarted, setGameStarted] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedWords, setSelectedWords] = useState([]); // Keep track of selected words
  const [timer, setTimer] = useState(30);
  const [submitted, setSubmitted] = useState(false);

  // Fetch questions from backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/questions")
      .then((response) => {
        if (Array.isArray(response.data)) {
          setQuestions(response.data);
        } else {
          console.error("Data is not an array:", response.data);
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching questions:", error);
        setLoading(false);
      });
  }, []);

  // Timer logic
  useEffect(() => {
    if (timer > 0 && !submitted) {
      const countdown = setTimeout(() => setTimer(timer - 1), 1000);
      return () => clearTimeout(countdown);
    }
  }, [timer, submitted]);

  // Start screen
  if (!gameStarted) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-100 text-center">
        <h1 className="text-3xl font-bold mb-4">Sentence Construction Game</h1>
        <p className="mb-6 max-w-xl text-gray-700">
          Select the correct words to fill in the blanks and form meaningful sentences.
        </p>
        <button
          onClick={() => setGameStarted(true)}
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Start Game
        </button>
      </div>
    );
  }

  // Handle option click (fill in the blanks)
  const handleOptionClick = (option) => {
    if (selectedWords.length < currentQuestion.question.split("___").length) {
      setSelectedWords([...selectedWords, option]); // Add the selected word to the list
    }
  };

  // Handle submit
  const handleSubmit = () => {
    setSubmitted(true);
  };

  // Handle next question
  const handleNext = () => {
    setSelectedWords([]);
    setSubmitted(false);
    setTimer(30);
    setCurrentQuestionIndex((prevIndex) => Math.min(prevIndex + 1, questions.length - 1));
  };

  if (loading) return <div className="text-center p-4">Loading...</div>;

  // Get the current question
  const currentQuestion = questions[currentQuestionIndex];

  // Split the sentence by blanks and insert the selected words at the correct places
  const filledSentence = currentQuestion.question.split("___").map((part, idx) => (
    <span key={idx}>
      {part}
      {selectedWords[idx] ? (
        <span className="bg-yellow-200 px-2 rounded">{selectedWords[idx]}</span>
      ) : (
        <span className="underline">&nbsp;&nbsp;&nbsp;&nbsp;</span>
      )}
    </span>
  ));

  return (
    <div className="max-w-3xl mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6 text-center">Sentence Construction Game</h1>

      {/* Display the filled-in sentence */}
      <div className="mb-6 p-4 border rounded-lg">
        <h2 className="mb-4 text-lg">{filledSentence}</h2>
      </div>

      {/* Timer */}
      <div className="mb-4 text-center text-xl">
        Time Left: {timer}s
      </div>

      {/* Options */}
      <div className="mb-6 p-4 border rounded-lg">
        <div className="grid grid-cols-2 gap-3">
          {currentQuestion.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleOptionClick(option)}
              className={`p-2 rounded-lg text-white font-semibold transition ${
                selectedWords.includes(option)
                  ? "bg-green-600"
                  : "bg-blue-500 hover:bg-blue-600"
              }`}
            >
              {option}
            </button>
          ))}
        </div>
      </div>

      {/* Submit button */}
      {!submitted && selectedWords.length === currentQuestion.question.split("___").length && (
        <div className="text-center">
          <button
            onClick={handleSubmit}
            className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
          >
            Submit Answer
          </button>
        </div>
      )}

      {/* Display results after submission */}
      {submitted && (
        <div className="mt-4 text-center">
          <p className="text-lg">
            {selectedWords.join(", ") === currentQuestion.answer.join(", ") ? (
              <span className="text-green-600 font-bold">Correct!</span>
            ) : (
              <span className="text-red-600 font-bold">
                Incorrect. Correct Answer: {currentQuestion.answer.join(", ")}
              </span>
            )}
          </p>
        </div>
      )}

      {/* Next button */}
      <div className="mt-6 text-center">
        {currentQuestionIndex < questions.length - 1 && !submitted && (
          <button
            onClick={handleNext}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            Next Question
          </button>
        )}
      </div>
    </div>
  );
};

export default App;
