import React, { useState, useEffect } from 'react';
import { fetchQuestions } from './data/api'; // Assuming this fetches from your API
import QuestionCard from './components/QuestionCard';
import ScoreScreen from './components/ScoreScreen';

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [loading, setLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState(30);

  useEffect(() => {
    const loadQuestions = async () => {
      const data = await fetchQuestions();
      setQuestions(data);
      setLoading(false);
    };
    loadQuestions();
  }, []);

  useEffect(() => {
    if (timeLeft === 0) {
      setGameOver(true);
    } else {
      const timer = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [timeLeft]);

  const handleAnswer = (isCorrect) => {
    if (isCorrect) setScore(score + 1);
    const next = currentIndex + 1;
    if (next < questions.length) {
      setCurrentIndex(next);
    } else {
      setGameOver(true);
    }
  };

  const restartGame = () => {
    setScore(0);
    setCurrentIndex(0);
    setGameOver(false);
    setTimeLeft(30);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Sentence Construction Game</h1>
      {loading ? (
        <div className="text-center">Loading...</div>
      ) : gameOver ? (
        <ScoreScreen score={score} total={questions.length} onRestart={restartGame} />
      ) : (
        <>
          <div className="text-center text-xl mb-4">Time Left: {timeLeft}s</div>
          <QuestionCard question={questions[currentIndex]} onAnswer={handleAnswer} />
        </>
      )}
    </div>
  );
}

export default App;
