import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedWords, setSelectedWords] = useState([]);
  const [timer, setTimer] = useState(30);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:5000/questions')
      .then((response) => setQuestions(response.data))
      .catch((err) => console.error(err));
  }, []);

  useEffect(() => {
    if (!submitted && timer > 0) {
      const countdown = setTimeout(() => setTimer((t) => t - 1), 1000);
      return () => clearTimeout(countdown);
    }
  }, [timer, submitted]);

  const currentQuestion = questions[currentQuestionIndex];

  const handleOptionClick = (word) => {
    if (selectedWords.length < (currentQuestion?.answer?.length || 0)) {
      setSelectedWords([...selectedWords, word]);
    }
  };

  const handleSubmit = () => {
    setSubmitted(true);
  };

  const handleNext = () => {
    setCurrentQuestionIndex((prev) => Math.min(prev + 1, questions.length - 1));
    setSelectedWords([]);
    setSubmitted(false);
    setTimer(30);
  };

  const handlePrev = () => {
    setCurrentQuestionIndex((prev) => Math.max(prev - 1, 0));
    setSelectedWords([]);
    setSubmitted(false);
    setTimer(30);
  };

  if (!currentQuestion) return <div className="text-center mt-10">Loading...</div>;

  const filledSentence = currentQuestion.question.split("___").map((part, idx) => (
    <span key={idx}>
      {part}
      {selectedWords[idx] ? <span className="bg-yellow-200 px-2 rounded">{selectedWords[idx]}</span> : <span className="underline">&nbsp;&nbsp;&nbsp;&nbsp;</span>}
    </span>
  ));

  return (
    <div className="max-w-3xl mx-auto p-4 bg-white rounded shadow-md mt-10">
      <h1 className="text-2xl font-bold text-center mb-4 text-blue-700">Sentence Construction Game</h1>
      <div className="flex justify-between mb-4">
        <button onClick={handlePrev} disabled={currentQuestionIndex === 0} className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded">Previous</button>
        <span className="text-red-500 font-bold">Time Left: {timer}s</span>
        <button onClick={handleNext} disabled={currentQuestionIndex === questions.length - 1} className="px-4 py-2 bg-blue-300 hover:bg-blue-400 rounded">Next</button>
      </div>
      <div className="mb-4 text-lg leading-relaxed">
        {filledSentence}
      </div>
      <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mb-4">
        {currentQuestion.options.map((option, idx) => (
          <button
            key={idx}
            onClick={() => handleOptionClick(option)}
            disabled={submitted || selectedWords.includes(option)}
            className={`px-4 py-2 rounded text-white ${selectedWords.includes(option) ? 'bg-green-500' : 'bg-blue-500 hover:bg-blue-600'}`}
          >
            {option}
          </button>
        ))}
      </div>
      {!submitted && selectedWords.length === currentQuestion.answer.length && (
        <button
          onClick={handleSubmit}
          className="w-full py-2 bg-green-600 text-white rounded hover:bg-green-700"
        >
          Submit Answer
        </button>
      )}
      {submitted && (
        <div className="mt-4 text-center">
          <p className="text-lg">
            {JSON.stringify(selectedWords) === JSON.stringify(currentQuestion.answer) ? (
              <span className="text-green-600 font-bold">Correct!</span>
            ) : (
              <span className="text-red-600 font-bold">Incorrect. Correct Answer: {currentQuestion.answer.join(', ')}</span>
            )}
          </p>
        </div>
      )}
    </div>
  );
};

export default App;
