import React, { useEffect, useState } from "react";
import Game from "./Game";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetching the data when the component mounts
  useEffect(() => {
    // Adjust the URL if you're using a local server or a remote API
    fetch("http://localhost:5000/questions")  // URL of the JSON data
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setQuestions(data.questions);
        setLoading(false); // Data is loaded, set loading to false
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []); // Empty dependency array ensures this runs only once on mount

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>Sentence Construction Game</h1>
      {/* Pass the fetched questions to the Game component */}
      <Game questions={questions} />
    </div>
  );
};

export default App;
