import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetching data from the backend
  useEffect(() => {
    axios
      .get("http://localhost:5000")  // This assumes your JSON Server is running at port 5000
      .then((response) => {
        setQuestions(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching questions:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container">
      <h1 className="text-center font-bold text-2xl mb-4">
        Sentence Construction Game
      </h1>
      {questions.map((question) => (
        <div key={question.questionId} className="mb-6">
          <h2 className="font-semibold mb-2">{question.question}</h2>
          <div className="grid grid-cols-2 gap-2">
            {question.options.map((option, index) => (
              <button
                key={index}
                className="bg-blue-500 text-white p-2 rounded-lg hover:bg-blue-600"
                onClick={() => {
                  // Handle answer selection here
                }}
              >
                {option}
              </button>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
