import axios from 'axios';

// Fetch questions from the server
export const fetchQuestions = async () => {
  try {
    const response = await axios.get("http://localhost:3001/data");
    
    // Log the full response to inspect the structure
    console.log("API Response:", response);

    // Check if the data structure is correct
    if (response.data && response.data.data && response.data.data.questions) {
      console.log("Fetched Questions:", response.data.data.questions);  // Log the questions array
      return response.data.data.questions;
    } else {
      console.error("Invalid response structure:", response);
      return [];  // Return an empty array if the structure is unexpected
    }
  } catch (error) {
    console.error("Error fetching questions:", error);
    return [];  // Return an empty array in case of an error
  }
};
