// App.js
import React, { useEffect, useState } from 'react';
import { fetchQuestions } from './components/api';  // Correct named import

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getQuestions = async () => {
      try {
        const data = await fetchQuestions();  // Fetch the questions
        setQuestions(data);
      } catch (error) {
        console.error('Error fetching questions:', error);
      } finally {
        setLoading(false);  // Set loading to false after the data is fetched
      }
    };

    getQuestions();
  }, []);

  return (
    <div>
      {loading ? (
        <h2>Loading questions...</h2>  // Display while loading
      ) : (
        <div>
          <h1>Questions</h1>
          <ul>
            {questions.length > 0 ? (
              questions.map((question, index) => (
                <li key={index}>{question.text}</li>  // Adjust based on your data
              ))
            ) : (
              <p>No questions available.</p>
            )}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
