import React from "react";

const StartPage = ({ onStart }) => {
  return (
    <div className="text-center bg-white p-6 rounded shadow-md max-w-lg w-full">
      <h1 className="text-3xl font-bold mb-4">ðŸ§  Sentence Construction Game</h1>
      <p className="mb-6 text-gray-700">Arrange the correct words to complete the sentence.</p>
      <button
        onClick={onStart}
        className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-700"
      >
        Start Game
      </button>
    </div>
  );
};

export default StartPage;
