import React, { useState } from 'react';

function QuestionCard({ question, onAnswer }) {
  const [selected, setSelected] = useState([]);
  const [locked, setLocked] = useState(false);
  const [answerCorrect, setAnswerCorrect] = useState(null); 
  const toggleWord = (word) => {
    if (locked) return;
    if (selected.includes(word)) {
      setSelected(selected.filter(w => w !== word));
    } else {
      setSelected([...selected, word]);
    }
  };

  const checkAnswer = () => {
    setLocked(true);
    const correct = JSON.stringify(selected) === JSON.stringify(question.correctAnswer);
    setAnswerCorrect(correct); 
    setTimeout(() => {
      onAnswer(correct);
      setSelected([]);
      setLocked(false);
      setAnswerCorrect(null); 
    }, 1000);
  };

  return (
    <div className="bg-white p-6 rounded shadow-md max-w-xl mx-auto">
      <p className="mb-4">{question.question}</p>
      <div className="flex flex-wrap gap-2 mb-4">
        {question.options.map((word, index) => (
          <button
            key={index}
            onClick={() => toggleWord(word)}
            className={`px-4 py-2 border rounded-full w-24 sm:w-32 ${selected.includes(word) ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'} ${locked ? 'cursor-not-allowed opacity-50' : ''}`}
            disabled={locked} 
          >
            {word}
          </button>
        ))}
      </div>
      <button
        onClick={checkAnswer}
        disabled={locked || selected.length !== 4}
        className="px-6 py-2 bg-green-600 text-white rounded"
      >
        Submit
      </button>
      {answerCorrect !== null && (
        <div className={`mt-4 text-xl ${answerCorrect ? 'text-green-600' : 'text-red-600'}`}>
          {answerCorrect ? 'Correct!' : 'Incorrect!'}
        </div>
      )}
    </div>
  );
}

export default QuestionCard;
