import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedWords, setSelectedWords] = useState([]);
  const [timeLeft, setTimeLeft] = useState(30);
  const [gameStarted, setGameStarted] = useState(false);
  const [gameFinished, setGameFinished] = useState(false);

  // Fetch questions from backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/questions") // Adjust the URL based on your backend setup
      .then((response) => {
        if (Array.isArray(response.data)) {
          setQuestions(response.data);
        } else {
          console.error("Data is not an array:", response.data);
        }
      })
      .catch((error) => {
        console.error("Error fetching questions:", error);
      });
  }, []);

  // Timer logic
  useEffect(() => {
    if (gameStarted && timeLeft > 0 && !gameFinished) {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [gameStarted, timeLeft, gameFinished]);

  const handleOptionClick = (option) => {
    // Only replace an empty spot in the sentence
    if (selectedWords.length < 4) {
      setSelectedWords((prevWords) => {
        const updatedWords = [...prevWords];
        const nextBlankIndex = updatedWords.findIndex((word) => word === "");
        if (nextBlankIndex !== -1) {
          updatedWords[nextBlankIndex] = option;
        }
        return updatedWords;
      });
    }
  };

  const handleUnselect = (index) => {
    const updatedWords = [...selectedWords];
    updatedWords[index] = "";
    setSelectedWords(updatedWords);
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedWords([]);
      setTimeLeft(30); // Reset timer for the next question
    } else {
      setGameFinished(true);
    }
  };

  const handleStartGame = () => {
    setGameStarted(true);
  };

  const currentQuestion = questions[currentQuestionIndex];

  if (!gameStarted) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-100 text-center">
        <h1 className="text-3xl font-bold mb-4">Sentence Construction Game</h1>
        <p className="mb-6 max-w-xl text-gray-700">
          Select the correct words to fill in the blanks and form meaningful sentences.
        </p>
        <button
          onClick={handleStartGame}
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Start Game
        </button>
      </div>
    );
  }

  if (gameFinished) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-100 text-center">
        <h1 className="text-3xl font-bold mb-4">Game Over</h1>
        <p className="text-gray-700">Your time has ended! Well done!</p>
      </div>
    );
  }

  // Replace blanks with selected words dynamically
  const sentenceWithBlanks = currentQuestion.question.split("____").map((part, index) => {
    if (selectedWords[index]) {
      return (
        <span key={index} className="bg-green-200 px-2 py-1 rounded">
          {selectedWords[index]}
        </span>
      );
    }
    return <span key={index} className="bg-gray-200 px-2 py-1 rounded">____</span>;
  });

  return (
    <div className="max-w-3xl mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6 text-center">
        Sentence Construction Game
      </h1>

      <div className="text-center mb-6">
        <p>Time Left: {timeLeft}s</p>
      </div>

      <div className="mb-8 p-4 border rounded-lg">
        <h2 className="mb-4 text-lg">{currentQuestion.question}</h2>
        <div className="grid grid-cols-2 gap-3">
          {currentQuestion.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleOptionClick(option)}
              className={`p-2 rounded-lg text-white font-semibold transition ${
                selectedWords.includes(option)
                  ? "bg-green-600"
                  : "bg-blue-500 hover:bg-blue-600"
              }`}
            >
              {option}
            </button>
          ))}
        </div>
      </div>

      <div className="mb-8">
        <p className="font-semibold">Fill in the blanks:</p>
        <div className="flex justify-center gap-4 mt-4">
          {sentenceWithBlanks}
        </div>
      </div>

      <div className="flex justify-between mt-4">
        {currentQuestionIndex < questions.length - 1 && (
          <button
            onClick={handleNextQuestion}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            Next Question
          </button>
        )}
      </div>
    </div>
  );
};

export default App;
