import React from 'react';

function ScoreScreen({ score, total, onRestart }) {
  // Calculate the percentage of correct answers
  const percentage = (score / total) * 100;

  // Determine feedback message based on the score
  const getFeedbackMessage = () => {
    if (percentage === 100) {
      return "Excellent!";
    } else if (percentage >= 75) {
      return "Great Job!";
    } else if (percentage >= 50) {
      return "Good Effort!";
    } else {
      return "Better Luck Next Time!";
    }
  };

  return (
    <div className="text-center p-6 bg-white rounded shadow-md max-w-lg mx-auto">
      <h2 className="text-3xl font-semibold text-gray-800 mb-4">Game Over!</h2>
      <p className="text-xl text-gray-600 mb-2">Your Score: {score} / {total}</p>
      <p className="text-xl text-green-600 mb-6">{getFeedbackMessage()}</p>
      <button
        onClick={onRestart}  // Trigger the restart function passed as prop
        className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
      >
        Play Again
      </button>
    </div>
  );
}

export default ScoreScreen;
