import React from 'react';
import { useNavigate } from 'react-router-dom';

const StartPage = () => {
  const navigate = useNavigate();  // Hook for navigation

  const startGame = () => {
    // Navigate to /game when the button is clicked
    navigate('/game');
  };

  return (
    <div className="text-center">
      <h1 className="text-2xl font-bold mb-4">ðŸ§  Sentence Construction Game</h1>
      <p className="mb-6">Arrange the correct words to complete the sentence.</p>
      <button 
        onClick={startGame}  // Make sure the button is linked to the function
        className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
      >
        Start Game
      </button>
    </div>
  );
};

export default StartPage;
