import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import StartGame from './StartGame';
import GamePage from './GamePage';
import axios from "axios";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [gameStarted, setGameStarted] = useState(false);
  const [selectedOptions, setSelectedOptions] = useState({});

  // Fetch questions from backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/questions")
      .then((response) => {
        if (Array.isArray(response.data)) {
          setQuestions(response.data);
        } else {
          console.error("Data is not an array:", response.data);
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching questions:", error);
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="text-center p-4">Loading...</div>;

  // Start screen
  if (!gameStarted) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-100 text-center">
        <h1 className="text-3xl font-bold mb-4">Sentence Construction Game</h1>
        <p className="mb-6 max-w-xl text-gray-700">
          Select the correct words to fill in the blanks and form meaningful sentences.
        </p>
        <button
          onClick={() => setGameStarted(true)}
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Start Game
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6 text-center">
        Sentence Construction Game
      </h1>

      {questions.map((question) => (
        <div key={question.questionId} className="mb-8 p-4 border rounded-lg">
          <h2 className="mb-4 text-lg">{question.question}</h2>
          <div className="grid grid-cols-2 gap-3">
            {question.options.map((option, index) => (
              <button
                key={index}
                onClick={() =>
                  setSelectedOptions({
                    ...selectedOptions,
                    [question.questionId]: option,
                  })
                }
                className={`p-2 rounded-lg text-white font-semibold transition ${
                  selectedOptions[question.questionId] === option
                    ? "bg-green-600"
                    : "bg-blue-500 hover:bg-blue-600"
                }`}
              >
                {option}
              </button>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
