import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedWords, setSelectedWords] = useState(["", "", "", ""]);
  const [timeLeft, setTimeLeft] = useState(30);
  const [gameStarted, setGameStarted] = useState(false);
  const [gameFinished, setGameFinished] = useState(false);
  const [score, setScore] = useState(0);

  // Fetch questions from the backend (your db.json or server)
  useEffect(() => {
    axios
      .get("http://localhost:5000/questions") // Adjust the URL based on your backend setup
      .then((response) => {
        if (Array.isArray(response.data)) {
          setQuestions(response.data);
        } else {
          console.error("Data is not an array:", response.data);
        }
      })
      .catch((error) => {
        console.error("Error fetching questions:", error);
      });
  }, []);

  // Timer logic
  useEffect(() => {
    if (gameStarted && timeLeft > 0 && !gameFinished) {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [gameStarted, timeLeft, gameFinished]);

  const handleOptionClick = (option) => {
    const updatedWords = [...selectedWords];
    const firstEmptyIndex = updatedWords.findIndex((word) => word === "");
    if (firstEmptyIndex !== -1) {
      updatedWords[firstEmptyIndex] = option;
      setSelectedWords(updatedWords);
    } else {
      // Allow unselecting the word if clicked again
      const wordIndex = updatedWords.indexOf(option);
      if (wordIndex !== -1) {
        updatedWords[wordIndex] = "";
        setSelectedWords(updatedWords);
      }
    }
  };

  const handleNextQuestion = () => {
    const currentQuestion = questions[currentQuestionIndex];
    let correctAnswers = 0;

    // Check how many answers are correct
    selectedWords.forEach((word, index) => {
      if (word === currentQuestion.correctAnswer[index]) {
        correctAnswers++;
      }
    });

    // Update score
    setScore((prevScore) => prevScore + correctAnswers);

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedWords(["", "", "", ""]);
      setTimeLeft(30); // Reset timer for the next question
    } else {
      setGameFinished(true);
    }
  };

  const handleStartGame = () => {
    setGameStarted(true);
  };

  const currentQuestion = questions[currentQuestionIndex];

  if (!gameStarted) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-100 text-center">
        <h1 className="text-4xl font-bold mb-4">Sentence Construction Game</h1>
        <p className="mb-6 text-lg text-gray-700">
          Select the correct words to fill in the blanks and form meaningful sentences.
        </p>
        <button
          onClick={handleStartGame}
          className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700"
        >
          Start Game
        </button>
      </div>
    );
  }

  if (gameFinished) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-100 text-center">
        <h1 className="text-3xl font-bold mb-6">Game Over!</h1>
        <p className="text-xl mb-4">
          Thank you for playing! Your final score is: {score} / {questions.length}
        </p>
        
        <div className="w-full max-w-xl mb-8">
          <h2 className="text-xl font-semibold mb-4">Your Feedback:</h2>
          {questions.map((question, index) => {
            const userAnswer = selectedWords;
            const correctAnswer = question.correctAnswer;
            const correct = userAnswer.every((word, i) => word === correctAnswer[i]);

            return (
              <div key={index} className="p-4 border rounded-lg mb-4">
                <h3 className="text-lg mb-2">{question.question}</h3>
                <p>
                  <span className={correct ? "text-green-600" : "text-red-600"}>
                    {correct ? "Correct" : "Incorrect"}
                  </span>
                </p>
                <p>Your Answer: {userAnswer.join(", ")}</p>
                <p>Correct Answer: {correctAnswer.join(", ")}</p>
              </div>
            );
          })}
        </div>

        <button
          onClick={handleStartGame}
          className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700"
        >
          Play Again
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6 text-center">Sentence Construction Game</h1>

      <div className="mb-8 p-4 border rounded-lg">
        <h2 className="mb-4 text-lg">
          {/* Render question with blanks */}
          {currentQuestion.question.split("____________").map((part, index) => (
            <span key={index}>
              {part}
              {selectedWords[index] && (
                <span className="font-semibold">{selectedWords[index]}</span>
              )}
              {index < 3 && selectedWords[index] === "" && " _____ "}
            </span>
          ))}
        </h2>

        <div className="grid grid-cols-2 gap-3">
          {/* Render options for each question */}
          {currentQuestion.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleOptionClick(option)}
              className="p-2 rounded-lg bg-blue-500 hover:bg-blue-600 text-white font-semibold"
            >
              {option}
            </button>
          ))}
        </div>
      </div>

      <div className="text-center mt-4">
        <p className="text-xl">Time Left: {timeLeft}s</p>
        {selectedWords.every(word => word !== "") && (
          <button
            onClick={handleNextQuestion}
            className="mt-4 bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700"
          >
            Next Question
          </button>
        )}
      </div>
    </div>
  );
};

export default App;
